// Generated by CoffeeScript 1.3.3

/*
// ==UserScript==
// @name        burasan auto dasu
// @description burasan auto dasu
// @namespace   http://github.com/derekbailey
// @include     http://*.3gokushi.jp/busyodas/busyodas.php
// @version     0.1
// ==/UserScript==
*/


(function() {
  var bt, createTempElement, createTextarea, draw, getAkiwaku, getBP, getCount, getSSID, main, msg;

  getSSID = function() {
    return document.querySelectorAll('input[name=ssid]')[0].value;
  };

  getAkiwaku = function() {
    return parseInt(document.querySelectorAll('.sysMes > strong')[2].textContent, 10);
  };

  getBP = function() {
    return document.querySelector('#status_right').textContent.match(/([0-9]+)/)[0];
  };

  createTempElement = function(parentElement) {
    var elm;
    elm = document.createElement('div');
    elm.id = 'TempElement';
    parentElement.appendChild(elm);
    return parentElement.querySelector('#TempElement');
  };

  createTextarea = function() {
    var ta;
    ta = document.createElement('textarea');
    ta.style.width = '90%';
    ta.style.height = '20em';
    ta.id = 'resultCard';
    document.body.insertBefore(ta, document.body.firstChild);
    ta = document.createElement('textarea');
    ta.style.width = '80%';
    ta.style.height = '1em';
    ta.id = 'resultMessage';
    document.body.insertBefore(ta, document.body.firstChild);
    ta = document.createElement('textarea');
    ta.style.width = '10%';
    ta.style.height = '1em';
    ta.id = 'resultCount';
    document.body.insertBefore(ta, document.body.firstChild);
    return true;
  };

  draw = function() {
    var xhr;
    xhr = new XMLHttpRequest();
    xhr.open('POST', 'http://' + location.hostname + '/busyodas/busyodas.php');
    xhr.onreadystatechange = function(res) {
      var message, rare, temp;
      if (xhr.readyState === 4 && xhr.status === 200) {
        temp = createTempElement(document.body);
        temp.innerHTML = res.target.responseText;
        message = temp.querySelectorAll('div.sysMes2 p.bushodas_card')[0].textContent;
        if (temp.querySelectorAll('span.rarerity_c').length) {
          rare = 'C';
        } else if (temp.querySelectorAll('span.rarerity_uc').length) {
          rare = 'UC';
        } else if (temp.querySelectorAll('span.rarerity_r').length) {
          rare = 'R';
        } else if (temp.querySelectorAll('span.rarerity_sr').length) {
          rare = 'SR';
        } else if (temp.querySelectorAll('span.rarerity_ur').length) {
          rare = 'UR';
        }
        document.querySelector('#resultCard').textContent += rare + message + '\n';
        return temp.parentNode.removeChild(temp);
      }
    };
    xhr.setRequestHeader('Content-Type', 'application/x-www-form-urlencoded');
    return xhr.send('ssid=' + getSSID() + '&send=send&got_type=0');
  };

  getCount = function() {
    var akiwaku, kaisuu;
    akiwaku = getAkiwaku();
    kaisuu = parseInt(getBP() / 100, 10);
    if (akiwaku <= 0 || kaisuu <= 0) {
      return 0;
    }
    if (kaisuu < akiwaku) {
      return kaisuu;
    } else {
      return akiwaku;
    }
  };

  msg = function(s) {
    return document.querySelector('#resultMessage').textContent = s;
  };

  main = function() {
    var count, drawTimer, sleep, time, waitTimer;
    count = getCount();
    sleep = 5;
    if (count <= 0) {
      return false;
    }
    createTextarea();
    document.querySelector('#resultCount').textContent = "Count: " + (count.toString());
    time = sleep - 1;
    waitTimer = setInterval(function() {
      if (time < 0) {
        time = sleep - 1;
      }
      msg("Waiting...(" + (time.toString()) + ")");
      return time--;
    }, 1000);
    return drawTimer = setInterval(function() {
      draw();
      count--;
      document.querySelector('#resultCount').textContent = "Count: " + (count.toString());
      if (count <= 0) {
        clearInterval(drawTimer);
        clearInterval(waitTimer);
        msg('End');
        return false;
      }
    }, sleep * 1000);
  };

  /*
  console.log('SSID: ' + getSSID());
  console.log('Waku: ' + getAkiwaku());
  console.log('BP: ' + getBP());
  console.log('Count: ' + getCount());
  console.log('createElement: ' + createTempElement(document.body));
  console.log('createTextarea: ' + createTextarea());
  msg('test');
  */


  bt = document.createElement('button');

  bt.textContent = 'AUTO DASU';

  bt.style.width = '100%';

  bt.addEventListener('mouseover', function() {
    return this.style.cursor = 'pointer';
  });

  bt.addEventListener('click', main);

  document.querySelector('#sidebar').insertBefore(bt, document.querySelector('#sidebar').firstChild);

}).call(this);
